mapper.update:
UPDATE iam_role
SET name        = #{name},
    remark      = #{remark},
    update_time = #{updateTime}
WHERE id = #{id}
  AND org_id = #{orgId}

mapper.delete:
DELETE
FROM iam_role
WHERE org_id = #{orgId}
  AND id = #{id}

DELETE
FROM iam_user_role
WHERE org_id = #{orgId}
  AND role_id = #{roleId}

mapper.select:
SELECT target_id roleId, permission_code permissionCode
FROM iam_authorization
WHERE org_id = #{orgId}
AND target_type = 1
AND target_id in

    #{roleId}

SELECT

FROM iam_role
WHERE org_id = #{orgId}
AND name = #{name}

mapper.delete:
DELETE
FROM iam_authorization
WHERE org_id = #{orgId}
  AND target_type = 1
  AND target_id = #{roleId}
